const extend = require('extend');

module.exports = function (opts) {
  var defaults = {
    extensions: {
      '.js': null,
      '.json': null
    },
    searchPaths: []
  };
  if (!opts) {
    opts = {};
  }
  if (opts.name) {
    if (!opts.processName) {
      opts.processName = opts.name;
    }
    if (!opts.configName) {
      opts.configName = opts.name + 'file';
    }
    if (!opts.moduleName) {
      opts.moduleName = opts.name;
    }
  }
  if (!opts.processName) {
    throw new Error('You must specify a processName.');
  }
  if (!opts.configName) {
    throw new Error('You must specify a configName.');
  }
  if (!opts.moduleName) {
    throw new Error('You must specify a moduleName.');
  }
  return extend(defaults, opts);
};
